import React, { Component } from "react";
import { Navigate } from "react-router-dom";
import AuthService from "../services/auth.service";

export default class Profile extends Component {

  constructor(props) {

    super(props);

    this.state = {
      redirect: null,
      userReady: false,
      currentUser: { username: "" }
    };

  }

  componentDidMount() {

    const currentUser = AuthService.getCurrentUser();
    console.log(currentUser.data.user_auth);

    if (!currentUser) {
      this.setState({ redirect: "/home" });
    }

    this.setState({ currentUser: currentUser.data.user_auth, userReady: true })

  }

  render() {

    if (this.state.redirect) {
      return <Navigate to={this.state.redirect} />
    }

    const { currentUser } = this.state;

    return (

      <div className="container">
        {(this.state.userReady) ?
        <div>
          <header className="jumbotron">
            <h3>
              <strong>{currentUser.data.user_auth.username}</strong> Profile
            </h3>
          </header>
          <p>
            <strong>Id:</strong>{" "}
            {currentUser.data.user_auth.user_id}
          </p>
          <p>
            <strong>Email:</strong>{" "}
            {currentUser.data.user_auth.email}
          </p>
          <strong>Authorities:</strong>
          <ul>
            {currentUser.data.user_auth.roles &&
              currentUser.data.user_auth.roles.map((role, index) => <li key={index}>{role}</li>)}
          </ul>
        </div> : null
        }
      </div>
    );

  }

}
